import React, { useState, useRef, useEffect } from "react";
import { useDispatch } from "react-redux";
import { signUpUser } from "../redux/actions/userActions";
import { useNavigate } from "react-router-dom";
import styles from "../assets/scss/registration.module.scss";
import Alert from "react-bootstrap/Alert";
import Button from "react-bootstrap/Button";
const Registrations = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: "",
    role: "",
  });
  const [clicked, setClicked] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const passwordConfirmationRef = useRef(null);
  const roleRef = useRef(null);
  const url = process.env.REACT_APP_REGISTRATION_URL;
  const [show, setShow] = useState(true);
  const fetchApi = (e) => {
    e.preventDefault();
    setClicked(true);
    setState({
      ...state,
      email: emailRef.current.value,
      password: passwordRef.current.value,
      passwordConfirmation: passwordConfirmationRef.current.value,
    });
  };

  useEffect(() => {
    if (clicked) {
      dispatch(signUpUser(url, state));
      navigate("/login");
    }
  }, [clicked, state, url, dispatch, navigate]);

  return (
    <div>
      <form className={styles.registrationForm} onSubmit={fetchApi}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          ref={emailRef}
          required
        ></input>

        <input
          type="password"
          name="password"
          placeholder="Password"
          ref={passwordRef}
          required
        ></input>

        <input
          type="password"
          name="passwordConfirmation"
          placeholder="Password Confirmation"
          ref={passwordConfirmationRef}
          required
        ></input>

        <select hidden name="roles">
          <option value="trainee" ref={roleRef}>
            Trainee
          </option>
        </select>

        <button className={styles.submitButton} type="submit">
          Submit
        </button>
        <Alert variant="danger" onClose={() => setShow(false)} dismissible>
          <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
          <p>
            Change this and that and try again. Duis mollis, est non commodo
            luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
            Cras mattis consectetur purus sit amet fermentum.
          </p>
        </Alert>
      </form>
    </div>
  );
};

export default Registrations;
