import { SIGN_IN_USER, SIGN_UP_USER, SIGN_OUT_USER } from "./constants";
import { LogoutApi, BasicApi } from "../../api/api";

export const signUpUser = (url, data) => async (dispatch) => {
  try {
    const user = await BasicApi(url, data);

    dispatch({ type: SIGN_UP_USER, user });
  } catch (err) {
    console.log(err);
  }
};

export const signInUser = (url, data) => async (dispatch) => {
  try {
    const user = await BasicApi(url, data);
    console.log(user);
    localStorage.setItem("token", user.data.token);
    dispatch({ type: SIGN_IN_USER, user });
  } catch (err) {
    console.log(err);
  }
};

export const signOutUser = (url) => async (dispatch) => {
  try {
    await LogoutApi(url);
    localStorage.removeItem("token");
    dispatch({ type: SIGN_OUT_USER });
  } catch (err) {
    console.log(err);
  }
};
