import { useEffect, useRef, useState } from "react"
import { useDispatch } from "react-redux"
import { useLocation } from "react-router-dom"
import { useNavigate } from "react-router-dom"
import { useSnackbar } from "react-simple-snackbar"
import Calendar from "react-calendar"

import { createBug } from "../../redux/actions/userActions"

import { Header, Spinner } from "../../components"
import { options } from "../../helpers/options"

import "react-calendar/dist/Calendar.css"
import styles from "../../assets/scss/createbug.module.css"

function CreateBug() {
  const titleRef = useRef()
  const statusRef = useRef()
  const typeRef = useRef()
  const navigate = useNavigate()
  const dispatch = useDispatch()
  const { state } = useLocation()
  const [openSnackbar] = useSnackbar(options)
  const [loading, setLoading] = useState(false)
  const [image, setImage] = useState("")
  const [date, setDate] = useState(new Date())

  const url = process.env.REACT_APP_CREATE_BUG
  const user = JSON.parse(localStorage.getItem(process.env.REACT_APP_PROFILE))
  console.log(user)

  const onChange = (e) => {
    setImage(e.target.files[0])
  }

  const onSubmit = (event) => {
    event.preventDefault()
    let formData = new FormData()
    formData.append("projectId", state?.id)
    formData.append("creator", user?._id)
    formData.append("title", titleRef.current.value)
    formData.append("status", statusRef.current.value)
    formData.append("type", typeRef.current.value)
    formData.append("deadline", date.toLocaleDateString())
    formData.append("file", image)
    dispatch(
      createBug(url, formData, setLoading, setImage, openSnackbar, navigate)
    )
  }
  useEffect(() => {}, [image])
  return (
    <>
      <Header />
      {loading ? (
        <Spinner />
      ) : (
        <div className="create-bug-wrapper ">
          <form className="d-flex flex-column w-50 m-4">
            <h2>BUG</h2>
            <input value={state?.id} />
            <label>Bug Creator</label>
            <input value={user?.name} disabled></input>
            <label>Title</label>
            <input ref={titleRef} required></input>
            <label>Type</label>

            <select ref={typeRef} required>
              <option>Feature</option>
              <option>Bug</option>
            </select>

            <label>Status</label>

            <select ref={statusRef} required>
              <option>New</option>
              <option>Started</option>
              {typeRef?.current?.value === "Bug" ? (
                <option>Resolved</option>
              ) : (
                <option>Completed</option>
              )}
            </select>
            <div>
              <label>Deadline</label> <br />
              <input value={date.toLocaleDateString()} disabled />
              <Calendar
                onChange={setDate}
                value={date}
                minDate={new Date()}
                defaultView="month"
              />
            </div>

            <label>Attach Screenshot</label>
            <input
              accept=".png, .gif"
              className="image-upload"
              type="file"
              name="image"
              onChange={onChange}
              width="100px"
              height="100px"
            />

            <img src={process.env.REACT_APP_LOCALHOST + image} alt=""></img>

            <button className={styles.createBugBtn} onClick={onSubmit}>
              Create Bug
            </button>
          </form>
        </div>
      )}
    </>
  )
}

export default CreateBug
