import { useRef } from "react"
import { useDispatch } from "react-redux"
import { useAuthorize } from "../../helpers/hooks/useAuthorize"
import useGetDevelopers from "../../helpers/hooks/useGetDevelopers"

import { createProjects } from "../../redux/actions/userActions"
import { Header } from "../index"

const CreateProjects = () => {
  const isAuthorized = useAuthorize()
  const developers = useGetDevelopers()
  const titleRef = useRef()
  const developerRef = useRef()
  const dispatch = useDispatch()
  const url = process.env.REACT_APP_CREATE_PROJECT
  const onSubmit = (event) => {
    event.preventDefault()
    dispatch(createProjects(url, formData, setLoading))
  }
  return (
    isAuthorized && (
      <>
        <Header />
        <div className="project-wrapper">
          <div className="card">
            <div className="card-title">
              <label>Project Title</label>
              <input ref={titleRef}> </input>
            </div>
            <select ref={developerRef}>
              <option value="" disabled selected>
                Select Developers
              </option>

              {developers &&
                developers.map((developer) => {
                  return (
                    <option value={developer.email}>{developer.email}</option>
                  )
                })}
            </select>
          </div>
          <button onClick={onSubmit}>Create Project</button>
        </div>
      </>
    )
  )
}

export default CreateProjects
