import React, { useState, useRef, useEffect } from "react"
import { useDispatch } from "react-redux"
import { signUpUser } from "../redux/actions/userActions"
import { useNavigate } from "react-router-dom"
import { useFormik } from "formik"
import { Spinner } from "../components"

import styles from "../assets/scss/registration.module.css"

const Registrations = () => {
  const [state, setState] = useState({
    name: "",
    email: "",
    password: "",
    passwordConfirmation: "",
    role: "",
  })
  const [clicked, setClicked] = useState(false)
  const [loading, setLoading] = useState(false)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const nameRef = useRef(null)
  const emailRef = useRef(null)
  const passwordRef = useRef(null)
  const passwordConfirmationRef = useRef(null)
  const roleRef = useRef(null)
  const url = process.env.REACT_APP_REGISTRATION_URL
  const formik = useFormik({
    initialValues: {
      name: "",
      email: "",
      password: "",
      passwordConfirmation: "",
      role: "",
    },
    onSubmit: (values) => {
      setClicked(true)
      setState({
        ...state,
        name: values.name,
        email: values.email,
        password: values.password,
        passwordConfirmation: values.passwordConfirmation,
        role: values.role,
      })
    },
  })

  useEffect(() => {
    if (clicked) {
      dispatch(signUpUser(url, state, setLoading, navigate))
      setLoading(true)
    }
  }, [clicked, state, url, dispatch, navigate])

  return loading ? (
    <Spinner />
  ) : (
    <form className={styles.registrationForm} onSubmit={formik.handleSubmit}>
      <input
        type="name"
        name="name"
        placeholder="Your Name"
        ref={nameRef}
        onChange={formik.handleChange}
        value={formik.values.name}
        required
      ></input>

      <input
        type="email"
        name="email"
        placeholder="Email"
        ref={emailRef}
        onChange={formik.handleChange}
        value={formik.values.email}
        required
      ></input>

      <input
        type="password"
        name="password"
        placeholder="Password"
        ref={passwordRef}
        onChange={formik.handleChange}
        value={formik.values.password}
        required
      ></input>

      <input
        type="password"
        name="passwordConfirmation"
        placeholder="Password Confirmation"
        ref={passwordConfirmationRef}
        onChange={formik.handleChange}
        value={formik.values.passwordConfirmation}
        required
      ></input>

      <select name="roles" ref={roleRef} onChange={formik.handleChange}>
        <option value="manager">Manager</option>
        <option value="developer">Developer</option>
        <option value="qa">QA</option>
      </select>

      <button className={styles.submitButton} type="submit">
        Submit
      </button>
    </form>
  )
}

export default Registrations
