import axios from "axios";

export const BasicApi = (url, data) => {
  const base_url = "http://localhost:3001";
  axios
    .post(
      base_url + url,
      {
        user: {
          email: data.email,
          password: data.password,
        },
      },
      { withCredentials: true }
    )
    .then((response) => response)
    .catch((err) => console.log(err));
};
