import { useRef, useState } from "react"
import { useDispatch } from "react-redux"
import { useAuthorize } from "../../helpers/hooks/useAuthorize"
import useGetDevelopers from "../../helpers/hooks/useGetDevelopers"
import { useSnackbar } from "react-simple-snackbar/dist"
import { createProject } from "../../redux/actions/userActions"
import { Header } from "../index"

const CreateProjects = () => {
  const isAuthorized = useAuthorize()
  const developers = useGetDevelopers()
  const titleRef = useRef()
  const [developersList, setDevelopersList] = useState([])
  const dispatch = useDispatch()
  const user = JSON.parse(localStorage.getItem("profile"))

  const [loading, setLoading] = useState(false)
  const url = process.env.REACT_APP_CREATE_PROJECT

  const addDeveloperToList = (developer) => {
    setDevelopersList(...developersList, developer)
    console.log(developersList)
  }

  const onSubmit = (event) => {
    event.preventDefault()
    dispatch(
      createProject(
        url,
        {
          title: titleRef.current.value,
          developer: developersList,
        },
        setLoading
      )
    )
  }
  return (
    isAuthorized && (
      <>
        <Header />
        <div className="project-wrapper">
          <div className="card">
            <input value={user._id}></input>
            <div className="card-title">
              <label>Project Title</label>
              <input ref={titleRef} />
            </div>

            {developers &&
              developers.map((developer, index) => {
                return (
                  <button
                    key={index}
                    value={developer.email}
                    onClick={() => addDeveloperToList(developer.email)}
                  >
                    {developer.email}
                  </button>
                )
              })}
          </div>
          <button onClick={onSubmit}>Create Project</button>
        </div>
      </>
    )
  )
}

export default CreateProjects
