import React, { useEffect, useState } from "react"
import { useDispatch } from "react-redux"
import { useSelector } from "react-redux"

import { authorizeLogIn } from "../../redux/actions/userActions"
import { AUTHORIZED_ROUTE } from "../../redux/actions/constants"
import { Header } from "../index"

const ViewRoadMaps = () => {
  const [authorizedRoute, setAuthorizedRoute] = useState(false)
  const dispatch = useDispatch()
  const message = useSelector((state) => state.user.message)

  useEffect(() => {
    const profile = JSON.parse(localStorage.getItem("profile"))
    dispatch(authorizeLogIn("admin/createemailtemplates", profile))
    if (message === AUTHORIZED_ROUTE) setAuthorizedRoute(true)
  }, [dispatch, message])

  return (
    authorizedRoute && (
      <>
        <Header />;<p> Welcome to Roadmaps</p>
      </>
    )
  )
}

export default ViewRoadMaps
