import React, { useState, useRef, useEffect } from "react";
import { useDispatch } from "react-redux";
import { signUpUser } from "../redux/actions/userActions";
import { useNavigate } from "react-router-dom";
import styles from "../assets/scss/registration.module.scss";

const Registrations = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: "",
    role: "",
  });
  const [clicked, setClicked] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const passwordConfirmationRef = useRef(null);
  const roleRef = useRef(null);
  const url = process.env.REACT_APP_REGISTRATION_URL;

  const fetchApi = (e) => {
    e.preventDefault();
    setClicked(true);
    setState({
      ...state,
      email: emailRef.current.value,
      password: passwordRef.current.value,
      passwordConfirmation: passwordConfirmationRef.current.value,
    });
  };

  useEffect(() => {
    if (clicked) {
      dispatch(signUpUser(url, state));
      navigate("/login");
    }
  }, [clicked, state, url, dispatch, navigate]);

  return (
    <div>
      <form className={styles.registrationForm} onSubmit={fetchApi}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          ref={emailRef}
          required
        ></input>

        <input
          type="password"
          name="password"
          placeholder="Password"
          ref={passwordRef}
          required
        ></input>

        <input
          type="password"
          name="passwordConfirmation"
          placeholder="Password Confirmation"
          ref={passwordConfirmationRef}
          required
        ></input>

        <select hidden name="roles">
          <option value="trainee" ref={roleRef}>
            Trainee
          </option>
        </select>

        <button className={styles.submitButton} type="submit">
          Submit
        </button>
        <div class="alert alert-danger alert-dismissible" role="alert">
          <div class="center-v">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              class="bi bi-exclamation-triangle-fill me-2"
              viewBox="0 0 16 16"
            >
              <path d="M8.982 1.566a1.13 1.13 0 0 0-1.96 0L.165 13.233c-.457.778.091 1.767.98 1.767h13.713c.889 0 1.438-.99.98-1.767L8.982 1.566zM8 5c.535 0 .954.462.9.995l-.35 3.507a.552.552 0 0 1-1.1 0L7.1 5.995A.905.905 0 0 1 8 5zm.002 6a1 1 0 1 1 0 2 1 1 0 0 1 0-2z" />
            </svg>
            Torus Kit custom hide effect on Bootstrap alert
          </div>
          <hr />
          <p class="mb-0 small opacity-70">
            Make sure you have entered email and password correctly.
          </p>
        </div>
      </form>
    </div>
  );
};

export default Registrations;
