import { useNavigate } from "react-router-dom"
import useGetProjects from "../../helpers/hooks/useGetProjects"
import Header from "../Header/Header"
const ViewProjects = () => {
  const projects = useGetProjects()
  const navigate = useNavigate()

  const handleReportBug = (id) => {
    navigate("/create-bugs", {
      state: {
        id: id,
      },
    })
  }
  console.log(projects)
  return (
    <>
      <Header />
      <div>
        {projects &&
          projects.map((project) => {
            return (
              <>
                <p>{project.title}</p>

                {project.bugsData.map((bug) => {
                  return (
                    <>
                      <b>BUG</b>
                      <p>Title: {bug.title}</p>
                      <p>Status: {bug.status}</p>
                      <p>Type: {bug.type}</p>
                      <img
                        src={`http://localhost:3000/${bug.image.path}`}
                        alt="Bug screenshot"
                        width="100px"
                        height="100px"
                      />
                    </>
                  )
                })}
                <br />
                <button onClick={() => handleReportBug(project._id)}>
                  Report Bug
                </button>
              </>
            )
          })}
      </div>
    </>
  )
}

export default ViewProjects
