import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import EmailTemplates from "./components/EmailTemplates/EmailTemplates";
import CreateRoadMaps from "./components/Roadmaps/CreateRoadMaps";
import ViewRoadMaps from "./components/Roadmaps/ViewRoadMaps";
import Login from "./auth/Login";
import Logout from "./auth/Logout";
import { Provider } from "react-redux";
import store from "./redux/store";
import Profile from "./components/Profile/Profile";
import SnackbarProvider from "react-simple-snackbar";

function App() {
  return (
    <SnackbarProvider>
      <Provider store={store}>
        <Router>
          <Routes>
            <Route path="/profile" element={<Profile />} />
            <Route path="/createroadmap" element={<CreateRoadMaps />} />
            <Route path="/viewroadmaps" element={<ViewRoadMaps />} />
            <Route path="/emailtemplates" element={<EmailTemplates />} />
            <Route path="/login" element={<Login />} />
            <Route path="/logout" element={<Logout />} />
            <Route path="/" element={<Home />} />
          </Routes>
        </Router>
      </Provider>
    </SnackbarProvider>
  );
}

export default App;
