import React, { useEffect, useState } from "react"
import { useDispatch } from "react-redux"
import { useSelector } from "react-redux"
import { useSnackbar } from "react-simple-snackbar"

import { Header } from "../index"

import { authorizeLogIn } from "../../redux/actions/userActions"
import { AUTHORIZED_ROUTE } from "../../redux/actions/constants"
import { options } from "../../helpers/options"

const EmailTemplates = () => {
  const [authorizedRoute, setAuthorizedRoute] = useState(false)
  const [openSnackbar] = useSnackbar(options)

  const dispatch = useDispatch()
  const message = useSelector((state) => state.user.message)
  useEffect(() => {
    const profile = JSON.parse(localStorage.getItem("profile"))
    dispatch(authorizeLogIn("admin/createemailtemplates", profile))
    if (message === AUTHORIZED_ROUTE) setAuthorizedRoute(true)
  }, [authorizedRoute])
  return authorizedRoute ? (
    <>
      <Header />
      <div> Design Email Templates</div>
    </>
  ) : (
    <p> You are not authorized</p>
  )
}
export default EmailTemplates
