import useGetBugs from "../../helpers/hooks/useGetBugs"
import { useAuthorize } from "../../helpers/hooks/useAuthorize"

import Bug from "./Bug"

import { Header } from "../index"

const ViewBugs = () => {
  const bugs = useGetBugs()
  console.log(bugs)
  const isAuthorized = useAuthorize("manager/createprojects")

  return (
    isAuthorized &&
    bugs && (
      <>
        <Header />
        <div className="view-bugs-wrapper">
          {bugs.map((bug) => {
            return (
              <>
                <div className="card">
                  <div className="card-title">{bug.title}</div>
                  <div className="card-body">
                    <h4>{bug.status}</h4>
                    <h4>{bug.type}</h4>
                    <h4>Bug deadline</h4>
                    <img src={`http://localhost:3000${bug.image.path}`} />
                  </div>
                </div>

                <button>Mark as Resolved</button>
              </>
            )
          })}
        </div>
      </>
    )
  )
}

export default ViewBugs
