import {
  AUTHORIZE_LOG_IN,
  SIGN_IN_USER,
  // SIGN_OUT_USER,
  SIGN_UP_USER,
} from "../actions/constants";

const initialState = {
  users: [],
};

export const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case SIGN_UP_USER:
      return { ...state, users: payload };
    case SIGN_IN_USER:
      localStorage.setItem("profile", payload);
      return { ...state, users: payload };
    case AUTHORIZE_LOG_IN:
      return { ...state, users: payload };
    // case SIGN_OUT_USER:
    //   return state;
    default:
      return state;
  }
};
