import React, { useState, useRef, useEffect } from "react";
import { useDispatch } from "react-redux";
import { signUpUser } from "../redux/actions/userActions";
import { useNavigate } from "react-router-dom";
import useRegistrationApi from "../api/register";
import styles from "../assets/scss/registration.module.scss";
const Registrations = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: "",
    role: "",
  });
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const passwordConfirmationRef = useRef(null);
  const url = process.env.REGISTRATION_URL;
  const data = useRegistrationApi(url, state);

  const fetchApi = (e) => {
    e.preventDefault();

    setState({
      ...state,
      email: emailRef.current.value,
      password: passwordRef.current.value,
      passwordConfirmation: passwordConfirmationRef.current.value,
    });
  };

  useEffect(() => {
    if (data && data.status === 200) {
      dispatch(signUpUser(data.data.user));
      navigate("/login");
    }
  }, [data, dispatch, navigate]);

  return (
    <div>
      <form className={styles.registrationForm} onSubmit={fetchApi}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          ref={emailRef}
          required
        ></input>

        <input
          type="password"
          name="password"
          placeholder="Password"
          ref={passwordRef}
          required
        ></input>

        <input
          type="password"
          name="passwordConfirmation"
          placeholder="Password Confirmation"
          ref={passwordConfirmationRef}
          required
        ></input>
        <select hidden name="roles">
          <option value="trainee">Trainee</option>
        </select>

        <button className={styles.submitButton} type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default Registrations;
