import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useSnackbar } from "react-simple-snackbar";

import { authorizeLogIn } from "../../redux/actions/userActions";
import { AUTHORIZED_ROUTE } from "../../redux/actions/constants";
import { Header } from "../index";
import { options } from "../../helpers/options";

const CreateRoadMaps = () => {
  const [authorizedRoute, setAuthorizedRoute] = useState(false);
  const [openSnackbar, closeSnackbar] = useSnackbar(options);
  const dispatch = useDispatch();
  const message = useSelector((state) => state.user.message);
  useEffect(() => {
    const profile = JSON.parse(localStorage.getItem("profile"));
    dispatch(authorizeLogIn("admin/createroadmaps", profile, openSnackbar));
    if (message === AUTHORIZED_ROUTE) setAuthorizedRoute(true);
    console.log("SNACKBAR");
  }, [dispatch, me]);
  return (
    authorizedRoute && (
      <>
        <Header />
        <div className="roadmap-wrapper">
          <div className="card">
            <div className="card-title">RoadMap</div>
            <div className="assigned-to-wrapper">
              <div className="assignee">
                US (on hover full name, on click a card with details)
              </div>
            </div>
            <div className="sprints">
              <div className="image">
                <img src=""></img>
              </div>
              <div className="color-labels"></div>
              <div className="desc"></div>
              <div className="checklist-wrapper">
                <li> Set up a meeting</li>
                <button>Assign Dates</button>
                <li> Meet with Usman</li>
                <button>Assign Dates</button>
                <button> Add a item</button>
                <div className="checklist-progress-bar"></div>
              </div>

              <div className="attachments-wrapper"></div>
              <div className="due-date"></div>
              <div className="comments"></div>
              <div className="activity-log"></div>
            </div>
          </div>
        </div>
      </>
    )
  );
};

export default CreateRoadMaps;
