import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./header.scss";

const Header = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  useEffect(() => {
    if (localStorage.getItem("profile")) setLoggedIn(true);
  }, []);
  return (
    loggedIn && (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand m-2" href="/">
          Buddy-App
        </a>
        <div>
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <Link to="/" className="link-style">
                Sign Up
              </Link>
            </li>
            <li className="nav-item active">
              <Link to="/login" className="link-style">
                Login
              </Link>
            </li>
            <li className="nav-item active">
              <Link to="/logout" className="link-style">
                Logout
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/createroadmap" className="link-style">
                Create Roadmaps
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/viewroadmaps" className="link-style">
                View Roadmaps
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/emailtemplates" className="link-style">
                Email Templates
              </Link>
            </li>
          </ul>
        </div>
      </nav>
    )
  );
};

export default Header;
