import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { signUpUser } from "../redux/actions/userActions";
import { useNavigate } from "react-router-dom";
import useRegistrationApi from "../api/register";
import useLoggedInApi from "../api/logged_in";

const Registrations = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    passwordConfirmation: "",
  });
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const passwordConfirmationRef = useRef(null);
  const url = "/registrations";
  const data = useRegistrationApi(url, state);
  const loggedIn = useLoggedInApi();

  const fetchApi = (e) => {
    e.preventDefault();
    setState({
      ...state,
      email: emailRef.current.value,
      password: passwordRef.current.value,
      passwordConfirmation: passwordConfirmationRef.current.value,
    });
  };

  useEffect(() => {
    console.log("log in", loggedIn);
    if (data && data.status === 200) {
      dispatch(signUpUser(data.data.user));
      navigate("/login");
    }
  }, []);

  return (
    <div>
      <form onSubmit={fetchApi}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          ref={emailRef}
          required
        ></input>

        <input
          type="password"
          name="password"
          placeholder="Password"
          ref={passwordRef}
          required
        ></input>

        <input
          type="password"
          name="passwordConfirmation"
          placeholder="Password Confirmation"
          ref={passwordConfirmationRef}
          required
        ></input>

        <button type="submit"> </button>
      </form>
    </div>
  );
};

export default Registrations;
