import axios from "axios";

const API = axios.create({ baseURL: process.env.REACT_APP_BASE_URL });

export const BasicApi = (url, data) => {
  return axios
    .post(url, {
      email: data.email,
      password: data.password,
    })
    .then((response) => response)
    .catch((err) => console.log("err", err));
};
