import {
  AUTHORIZE_LOG_IN,
  SIGN_IN_USER,
  SIGN_OUT_USER,
  SIGN_UP_USER,
} from "../actions/constants";

const initialState = {
  users: [],
};

export const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case SIGN_UP_USER:
      return { ...state, users: payload };
    case SIGN_IN_USER:
      localStorage.setItem("profile", JSON.stringify(payload.data.body));
      localStorage.setItem("token", JSON.stringify(payload.data.token));
      return { ...state, users: payload };
    case AUTHORIZE_LOG_IN:
      console.log(payload);
      return { ...state, users: payload };
    case SIGN_OUT_USER:
      localStorage.removeItem("profile");
      localStorage.removeItem("token");
      return state;
    default:
      return state;
  }
};
