import { useEffect, useRef, useState } from "react"
import { useDispatch } from "react-redux"
import { createBug } from "../../redux/actions/userActions"

const CreateBug = () => {
  const titleRef = useRef()
  const statusRef = useRef()
  const typeRef = useRef()
  const imageRef = useRef(null)
  const [loading, setLoading] = useState(false)
  const [image, setImage] = useState("")
  const dispatch = useDispatch()
  const url = process.env.REACT_APP_CREATE_BUG
  const onChange = (e) => {
    setImage(e.target.files[0])
  }
  const onSubmit = (event) => {
    event.preventDefault()
    let formData = new FormData()
    formData.append("title", titleRef.current.value)
    formData.append("status", statusRef.current.value)
    formData.append("type", typeRef.current.value)
    formData.append("file", image)
    dispatch(createBug(url, formData, setLoading, setImage))
  }
  useEffect(() => {
    console.log(image)
  }, [image])
  return (
    <>
      <div className="create-bug-wrapper">
        <form action="/create-bug" method="post" encType="multipart/form-data">
          <h2>BUG</h2>
          <label>Bug Creator</label>
          <input></input>
          <label>Assign Developer on this bug</label>
          <input></input>
          <label>Title</label>
          <input ref={titleRef}></input>
          <label>Status</label>
          <select ref={statusRef}>
            <option>New</option>
            <option>Started</option>
            <option>Resolved</option>
          </select>
          {/* depends on type */}
          <label>Deadline</label>
          <label>Type</label>
          <select ref={typeRef}>
            <option>Feature</option>
            <option>Bug</option>
          </select>

          <label>Attach Screenshot</label>
          <input
            accept=".png, .gif"
            className="image-upload"
            type="file"
            name="image"
            onChange={onChange}
          />
          <img src={`http://localhost:3001/${image}`} alt=""></img>
          {/* only .gif and .png */}
          <button onClick={onSubmit}> Create Bug</button>
        </form>
      </div>
    </>
  )
}

export default CreateBug
