import { useState, useRef } from "react"
import { useDispatch } from "react-redux"
import { useNavigate } from "react-router-dom"
import { useSnackbar } from "react-simple-snackbar"
import { useFormik } from "formik"
import * as yup from "yup"

import { options } from "../helpers/options"
import { signInUser } from "../redux/actions/userActions"
import { Header, Spinner } from "../components"

import { LoginStyles } from "../assets/index"

const Login = () => {
  const emailRef = useRef(null)
  const passwordRef = useRef(null)
  const [openSnackbar] = useSnackbar(options)
  const user = localStorage.getItem("profile")
  const [loading, setLoading] = useState(false)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const url = process.env.REACT_APP_SESSION_URL

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: yup.object({
      email: yup
        .string()
        .email("Invalid Email Address")
        .required("Email is required"),
      password: yup
        .string()
        .required("Password is required")
        .min(5, "Length should be greater than 5"),
    }),
    onSubmit: (values) => {
      setLoading(true)
      dispatch(
        signInUser(
          url,
          {
            email: emailRef.current.value,
            password: passwordRef.current.value,
          },
          setLoading,
          openSnackbar
        )
      )
    },
  })
  const fetchApi = (e) => {
    e.preventDefault()
    setLoading(true)
    dispatch(
      signInUser(
        url,
        {
          email: emailRef.current.value,
          password: passwordRef.current.value,
        },
        setLoading,
        openSnackbar
      )
    )
  }
  if (user) navigate("/")
  return (
    <div>
      <Header />
      {loading ? (
        <Spinner />
      ) : (
        <form className={LoginStyles.loginForm} onSubmit={formik.handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={formik.handleChange}
            value={formik.value.email}
          ></input>

          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={formik.handleChange}
            value={formik.value.password}
          ></input>

          <button className={LoginStyles.submitButton} type="submit">
            Submit
          </button>
        </form>
      )}
    </div>
  )
}

export default Login
