import {
  SIGN_IN_USER,
  SIGN_UP_USER,
  SIGN_OUT_USER,
  AUTHORIZE_LOG_IN,
} from "./constants";
import { LogoutApi, AuthLoginApi, AuthApi } from "../../api/api";

export const signUpUser =
  (url, data, setLoading, navigate) => async (dispatch) => {
    try {
      const user = await AuthApi(url, data);

      dispatch({ type: SIGN_UP_USER, user });
      setLoading(false);
      navigate("/login");
    } catch (err) {}
  };

export const signInUser =
  (url, data, setLoading, openSnackbar) => async (dispatch) => {
    try {
      console.log("USER ACTIONS");
      const user = await AuthApi(url, data);
      localStorage.setItem("profile", JSON.stringify(user.data.body));
      localStorage.setItem("token", JSON.stringify(user.data.token));
      setLoading(false);
      openSnackbar("You have sucessfully Signed In");
      dispatch({ type: SIGN_IN_USER, payload: user });
    } catch (err) {
      openSnackbar(err);
    }
  };

export const authorizeLogIn = (url, data) => async (dispatch) => {
  try {
    const user = await AuthLoginApi(url, data);
    dispatch({ type: AUTHORIZE_LOG_IN, payload: user });
  } catch (err) {
    console.log(err);
    const error = err;
  }
};

export const signOutUser = (url, setLoading) => async (dispatch) => {
  try {
    await LogoutApi(url);
    localStorage.removeItem("profile");
    localStorage.removeItem("token");
    dispatch({ type: SIGN_OUT_USER });
    setLoading(false);
    openSnackbar("You have sucessfully Logged Out");
  } catch (err) {
    console.log(err);
    const error = err;
  }
};
