import React, { useEffect } from "react";
import Header from "../header/Header";
import { useDispatch } from "react-redux";
import { authorizeLogIn } from "../../redux/actions/userActions";
const CreateRoadMaps = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    const profile = JSON.parse(localStorage.getItem("profile"));
    dispatch(authorizeLogIn("/profile", profile));
  }, [dispatch]);
  return (
    <>
      <Header />
      <div className="roadmap-wrapper">
        <div className="card">
          <div className="card-title">RoadMap</div>
          <div className="assigned-to-wrapper">
            <div className="assignee">
              US (on hover full name, on click a card with details)
            </div>
          </div>
          <div className="sprints">
            <div className="image">
              <img src=""></img>
            </div>
            <div className="color-labels"></div>
            <div className="desc"></div>
            <div className="checklist-wrapper">
              <li> Set up a meeting</li>
              <button>Assign Dates</button>
              <li> Meet with Usman</li>
              <button>Assign Dates</button>
              <button> Add a item</button>
              <div className="checklist-progress-bar"></div>
            </div>

            <div className="attachments-wrapper"></div>
            <div className="due-date"></div>
            <div className="comments"></div>
            <div className="activity-log"></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CreateRoadMaps;
