import {
  SIGN_IN_USER,
  SIGN_UP_USER,
  SIGN_OUT_USER,
} from "../constants/action-types";
import { BasicApi } from "../../api/api";

export const signUpUser = () => async (dispatch, url, data) => {
  console.log(data);
  try {
    const user = await BasicApi(url, data);

    dispatch({ type: SIGN_UP_USER, user });
  } catch (err) {
    console.log(err);
  }
};

export const signInUser = (user) => {
  return {
    type: SIGN_IN_USER,
    payload: user,
  };
};

export const signOutUser = (user) => {
  return {
    type: SIGN_OUT_USER,
    payload: user,
  };
};
